import React from "react";
import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
  NormalText
} from "../../styledComponents";
import userPhoto from "../../../public/static/sandy.jpeg";
import { Avatar, Divider, Chip } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";

const useStyles = makeStyles(theme => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    borderRadius: 0
  }
}));
const TicketWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  padding-top: 10px;
  padding-left: 15px;
  padding-right: 15px;
  height: 300px;
  min-width: 230px;
  border: 1px solid #d5d8dc;
  position: relative;
  margin-bottom: 20px;
  border-radius: 2px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const Tag = styled(Container)`
  flex: 1;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  padding: 5px;
  margin-right: 5px;
`;
const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: row;
  width: 100%;
  align-items: center;
  background-color: transparent;
`;

const ImageWrapper = styled(Container)`
  direction: flex;
  flex-direction: row;
  flex: 1;
  width: 100%;
  /* align-items: center; */
  background-color: transparent;
  padding: 0px;
  margin-top: 15px;
`;

const MiniTicketItem = ({ ticket }) => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [scroll, setScroll] = React.useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const [hover, setHover] = React.useState(false);

  const handleHover = () => {
    setHover(true);
  };
  const handleHoverOut = () => {
    setHover(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  return (
    <div style={{ flex: 0 }}>
      <TicketWrapper>
        <CardHeader>
          <Avatar src={userPhoto} className={classes.small} />
          <Container
            flex={1}
            column
            color="transparent"
            customStyles={{
              paddingLeft: 10,
              paddingRight: 5
            }}
          >
            <Text customStyles={{ fontSize: "14px", fontWeight: "500" }}>
              {ticket.title}
            </Text>
            <CaptionText customStyles={{ color: "#0078D4" }}>
              {ticket.date}
            </CaptionText>
          </Container>
        </CardHeader>

        <ImageWrapper>
          <Container flex={1} middle center customStyles={{ padding: 40 }}>
            <Avatar
              src={userPhoto}
              style={{ width: "100%", height: "auto", borderRadius: 0 }}
            />
          </Container>
        </ImageWrapper>
      </TicketWrapper>
      <Dialog
        fullScreen={fullScreen}
        onClose={handleClose}
        classes={{
          paper: classes.dialogPaper
        }}
        aria-labelledby="customized-dialog-title"
        open={open}
        scroll={scroll}
      >
        <DialogTitle
          id="scroll-dialog-title"
          classes={{
            root: classes.dialogActionStyle
          }}
        >
          <Container
            row
            center
            right
            color="transparent"
            space="space-between"
            customStyles={{
              paddingTop: 15,
              paddingBottom: 7,
              paddingRight: 10,
              paddingLeft: 20
            }}
          >
            <Container color="transparent" column middle center flex={1}>
              <Header>Add House to a building</Header>
            </Container>
            <CloseBtn onClick={handleClose}>
              <CloseIcon style={{ fontSize: 23 }} />
            </CloseBtn>
          </Container>
        </DialogTitle>
        <DialogContent
          classes={{
            root: classes.dialogContentStyle
          }}
        >
          <Container
            flex={1}
            column
            color="transparent"
            customStyles={{ paddingTop: 10, paddingBottom: 10 }}
          >
            <Container
              customStyles={{ paddingLeft: "40px" }}
              row
              center
              color="transparent"
            >
              <Avatar src={userPhoto} className={classes.avatarStyle} />
              <CaptionText customStyles={{ marginLeft: 20 }}>
                You are adding Barcelona
              </CaptionText>
            </Container>
            <Form>
              <fieldset
                style={{
                  display: "flex",
                  paddingRight: 0,
                  paddingLeft: 0,
                  paddingBottom: 10,
                  border: "none",
                  flex: 1
                }}
              >
                <Container row color="transparent" flex={1}>
                  <Container
                    flex={1}
                    column
                    middle
                    color="transparent"
                    customStyles={{ paddingRight: 10 }}
                  >
                    <Input placeholder="Create a new building" type="text" />
                  </Container>
                  <Container
                    flex={false}
                    column
                    color="transparent"
                    customStyles={{ paddingTop: 10 }}
                  >
                    <PrimaryButton>CREATE</PrimaryButton>
                  </Container>
                </Container>
              </fieldset>
            </Form>
            <Container
              column
              customStyles={{ padding: "10px 40px" }}
              color="transparent"
            >
              <Container flex={false} column color="transparent">
                <Container
                  color="transparent"
                  row
                  center
                  flex={1}
                  customStyles={{
                    paddingTop: 5,
                    paddingBottom: 15,
                    paddingLeft: 0,
                    paddingRight: 5
                  }}
                >
                  <Container row flex={1} center color="transparent">
                    <Avatar
                      src={userPhoto}
                      className={classes.buildingImageStyle}
                    />
                    <Container column flex={1} color="transparent">
                      <SubHeader
                        customStyles={{
                          fontSize: 12,
                          fontWeight: "600",
                          marginBottom: 0
                        }}
                      >
                        QINGCHENG VILLAS
                      </SubHeader>
                      <CaptionText>2 houses</CaptionText>
                    </Container>
                  </Container>

                  <AddBtn onMouseOver={handleHover} onMouseOut={handleHoverOut}>
                    {hover ? (
                      <CheckIcon style={{ fontSize: 18, color: "green" }} />
                    ) : (
                      <Add style={{ fontSize: 18 }} />
                    )}
                  </AddBtn>
                </Container>
                <Divider style={{ height: "1px" }} />
              </Container>
              <Container flex={false} column color="transparent">
                <Container
                  color="transparent"
                  row
                  center
                  flex={1}
                  customStyles={{
                    paddingTop: 5,
                    paddingBottom: 15,
                    paddingLeft: 0,
                    paddingRight: 5
                  }}
                >
                  <Container row flex={1} center color="transparent">
                    <Avatar
                      src={userPhoto}
                      className={classes.buildingImageStyle}
                    />
                    <Container column flex={1} color="transparent">
                      <SubHeader
                        customStyles={{
                          fontSize: 12,
                          fontWeight: "600",
                          marginBottom: 0
                        }}
                      >
                        QINGCHENG VILLAS
                      </SubHeader>
                      <CaptionText>2 houses</CaptionText>
                    </Container>
                  </Container>

                  <AddBtn onMouseOver={handleHover} onMouseOut={handleHoverOut}>
                    {hover ? (
                      <CheckIcon style={{ fontSize: 18, color: "green" }} />
                    ) : (
                      <Add style={{ fontSize: 18 }} />
                    )}
                  </AddBtn>
                </Container>
                <Divider style={{ height: "1px" }} />
              </Container>
            </Container>
          </Container>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default MiniTicketItem;
