import React from "react";
import styled from "styled-components";
import { fade } from "@material-ui/core/styles";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import PhoneIcon from "@material-ui/icons/Mail";
import DotIcon from "@material-ui/icons/FiberManualRecord";
import ArrowIcon from "@material-ui/icons/ArrowForward";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import MiniTicketItem from "./MiniTicketItem";
import MbanzaLogo from "../../../public/static/mbanza.png";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 2px 15px;
  margin: 3px 3px;
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 1;
  padding-top: 30px;
  max-height: calc(100vh - 115px);
  overflow: scroll;
  ::-webkit-scrollbar {
    display: none;
  }
`;

const Overview = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };

  const {
    house_type,
    managingAgent,
    house_status,
    appliances,
    ext_ft,
    utilities,
  } = house;
  return (
    <Wrapper>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <ViewIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            At a glance
          </SubHeader>
        </Container>
      </Container>

      <Container
        row
        flex={1}
        customStyles={{
          marginTop: 10,
          marginBottom: 20,
        }}
        color="transparent"
      >
        <Container
          flex={0.5}
          row
          customStyles={{
            borderRadius: 3,
            padding: 20,
            marginRight: 8,
          }}
          color="white"
        >
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%", paddingTop: 2 }}
          >
            <DotIcon style={{ fontSize: 16 }} />
          </Container>
          <Container
            column
            space={"space-between"}
            flex={0.8}
            color="transparent"
          >
            <SubText
              customStyles={{
                fontSize: 16,

                marginBottom: 0,
                marginTop: 0,
              }}
            >
              Tenant
            </SubText>
            <Container
              color="transparent"
              customStyles={{ paddingTop: 10, paddingBottom: 10 }}
            >
              <SubHeader
                customStyles={{
                  fontSize: 18,
                }}
              >
                Rene La
              </SubHeader>
            </Container>
            <Container
              row
              center
              color="transparent"
              customStyles={{ paddingTop: 5, paddingBottom: 5 }}
            >
              <Avatar
                style={{
                  backgroundColor: fade("#F7DC6F", 0.3),
                  width: 25,
                  height: 25,
                  marginRight: 8,
                }}
              >
                <MailIcon style={{ fontSize: 15, color: "#F1C40F" }} />
              </Avatar>
              <SubText>renela@gmail.com</SubText>
            </Container>
            <Container
              row
              center
              color="transparent"
              customStyles={{ paddingTop: 1, paddingBottom: 1 }}
            >
              <Avatar
                style={{
                  backgroundColor: fade("#F7DC6F", 0.3),
                  width: 25,
                  height: 25,
                  marginRight: 8,
                }}
              >
                <MailIcon style={{ fontSize: 15, color: "#F1C40F" }} />
              </Avatar>
              <SubText>15689622112</SubText>
            </Container>
          </Container>
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%", paddingTop: 2 }}
          >
